    if(
      length(c(dynamiccalcs,tdpredcalcs)) > 0 | 
        intoverpop | nldynamics==TRUE
    ) { message('Using nonlinear Kalman filter for dynamics'); ukf <- TRUE}

    nlmeasurement <- nlcontrol$nlmeasurement
